mapper.select:
select * from examPaper where 1 = 1

	and examId = #{examId}


	and studentId = #{studentId}


	and status = #{status}


	limit #{offset},#{pageSize}

select count(*) from examPaper where 1 = 1

	and examId = #{examId}


	and studentId = #{studentId}


	and status = #{status}

select ep.id as ep_id,ep.examId as ep_examId,ep.studentId as ep_studentId,ep.status as ep_status, ep.totalScore as ep_totalScore,ep.score as ep_score,ep.subScoreTotal as ep_subScoreTotal,ep.finalScore as ep_finalScore,ep.startExamTime as ep_startExamTime, ep.endExamTime as ep_endExamTime,ep.useTime as ep_useTime,ep.createTime as ep_createTime, e.id as e_id,e.name as e_name,e.subjectId as e_subjectId,e.startTime as e_startTime,e.endTime as e_endTime, e.avaliableTime as e_avaliableTime,e.questionNum as e_questionNum,e.totalScore as e_totalScore, e.passScore as e_passScore,e.singleQuestionNum as e_singleQuestionNum,e.muiltQuestionNum as e_muiltQuestionNum, e.chargeQuestionNum as e_chargeQuestionNum,e.paperNum as e_paperNum,e.examedNum as e_examedNum, e.passNum as e_passNum,e.createTime as e_createTime from examPaper ep,exam e where ep.examId = e.id and ep.studentId = #{studentId} and status = 1
and e.name like '%${name}%'
and e.subjectId = #{subjectId}
order by ep.createTime desc
limit #{offset},#{pageSize}

select count(*) from examPaper ep,exam e where ep.examId = e.id and ep.studentId = #{studentId}

	and e.name like '%${name}%'


	and e.subjectId = #{subjectId}
